INSERT INTO public."subCategories" (id, name, description, "createdAt", "updatedAt", "posterImageUrl", "last_editedBy", short_description, custom_url, "categoryId", "Banners", "BannerText", "BannerHeading", "leftHeading", "categoryText", "categoryHeroImages", "categoryHeroToptext", "categoryHeroHeading", "categoryHeroText", "categoryFaqs", "collectionHeading", "collectionMainHeading", "QualityHeadings", "QualityText", "QualityImages", "CustomHeading", "CustomText", "Product_Section_heading", "bottomText", "bodyHeading", "bodyMainHeading", "bodyText", "explore_Heading", explore_main_heading, explore_description, "professionalServiceImage", "Canonical_Tag", "Meta_Description", "Meta_Title", "breadCrum", "seoSchema", status) VALUES (6, 'Wallpapers', '', '2025-05-07 05:34:04.756', '2026-01-07 11:43:21.851', '{"altText": "Wallpapers", "imageUrl": "https://res.cloudinary.com/dsnhtfuef/image/upload/v1764588614/uploads/vpllvpgtr3upqitvbydi.webp", "public_id": "uploads/vpllvpgtr3upqitvbydi", "resource_type": "image"}', NULL, '', 'wallpapers', 6, '{"altText": "Wallpapers", "imageUrl": "https://twoguysprod.s3.eu-north-1.amazonaws.com/1751541066967-s3", "public_id": "1751541066967-s3", "resource_type": "image"}', 'Endless Designs, Easy to Install, Built to Last, Perfect </br>for Every Wall and Interior Style.', 'Two Guys Wallpaper', '', '{}', '{"{\"imageUrl\": \"https://res.cloudinary.com/dsnhtfuef/image/upload/v1764587241/uploads/blhorywf4hdawqzmjtdj.webp\", \"public_id\": \"uploads/blhorywf4hdawqzmjtdj\", \"resource_type\": \"image\"}"}', '', 'Discover Stylish, Durable, and Easy-to-Maintain Wallpapers Perfect for Modern Homes in Dubai & UAE
', '{"{\"name\": \"\", \"detail\": \"Looking to give your walls a fresh, stylish look? Our wallpapers come in a variety of colours, patterns, and textures, so you can easily find the perfect fit for your taste, whether you want something calm and relaxing or bold and eye-catching. We also have special designs for kids’ rooms, offices, gyms, and schools. For kids’ rooms, we have fun and playful cartoon designs that make their space lively and cheerful. For homes and offices, like living rooms or workspaces, we offer elegant patterns and textures that perfectly match your interior style. \"}","{\"name\": \"\", \"detail\": \"Our wallpapers are durable, scratch- and stain-resistant, and super easy to clean, so they can handle busy areas while staying beautiful. With finishes like smooth, matte, glossy, or textured, you can create the exact vibe you want. Plus, they’re easy to install and long-lasting, perfect for homes, villas, apartments, and workspaces. Stylish, practical, and versatile, our wallpapers make any room look amazing.\"}"}', '{}', 'Collection', 'Explore Our Wide Collection of Wallpapers – From Kids’ Cartoon Designs to Elegant Patterns for Homes and Offices', '{}', '{}', '{}', '{}', '{}', 'Collection', 'Modern Wallpapers Design : Meet Where Creativity speaks with booming Art (60%)', '', '', '', '', 'Seamless, Stylish, and Long-Lasting Walls Every Time', 'Enjoy hassle-free wallpaper installation with our skilled team. We ensure perfect alignment, smooth finishes, and durable results that bring out the best in your interiors.', '{"imageUrl": "https://res.cloudinary.com/dsnhtfuef/image/upload/v1746684489/uploads/iehax8dt2l05ghctjzro.png", "public_id": "uploads/iehax8dt2l05ghctjzro", "resource_type": "image"}', '', 'Get wallpaper designs at Two Guys Dubai. From kids’ cartoon themes to elegant patterns for homes & offices, our wallpapers are stylish and easy to maintain.
', 'Stylish Wallpapers in Dubai | Modern Designs – Two Guys ', NULL, NULL, 'PUBLISHED');
INSERT INTO public."subCategories" (id, name, description, "createdAt", "updatedAt", "posterImageUrl", "last_editedBy", short_description, custom_url, "categoryId", "Banners", "BannerText", "BannerHeading", "leftHeading", "categoryText", "categoryHeroImages", "categoryHeroToptext", "categoryHeroHeading", "categoryHeroText", "categoryFaqs", "collectionHeading", "collectionMainHeading", "QualityHeadings", "QualityText", "QualityImages", "CustomHeading", "CustomText", "Product_Section_heading", "bottomText", "bodyHeading", "bodyMainHeading", "bodyText", "explore_Heading", explore_main_heading, explore_description, "professionalServiceImage", "Canonical_Tag", "Meta_Description", "Meta_Title", "breadCrum", "seoSchema", status) VALUES (20, 'SPC Flooring', '', '2025-05-12 09:19:47.754', '2026-01-09 11:14:01.589', '{"imageUrl": "https://res.cloudinary.com/dsnhtfuef/image/upload/v1764329305/uploads/ted1xawd15csapnsdl5n.webp", "public_id": "uploads/ted1xawd15csapnsdl5n", "resource_type": "image"}', NULL, '', 'spc', 7, '{"imageUrl": "https://res.cloudinary.com/dsnhtfuef/image/upload/v1747041576/uploads/s6v6zj0wpl6xa5vr2zx2.jpg", "public_id": "uploads/s6v6zj0wpl6xa5vr2zx2", "resource_type": "image"}', 'SPC Flooring: The Smart Choice for Modern Living', 'Water Resistant. Durable. Stylish.', '', '{"{\"name\": \"Flooring Dubai: Vast and Extensive  Personalised  Flooring Options\", \"detail\": \"Transform the beauty of your space with our top-notch flooring in Dubai, a curated premium range that starts with wooden flooring and covers SPC flooring, luxury vinyl flooring, PVC flooring, laminate flooring, carpet tiles, parquet flooring, rubber flooring, and goes up to WPC. Call us at 04-252-2025 or fill out the form to book a free consultation, and our team will visit you with the samples to let you explore the types of flooring options we offer.\"}"}', '{"{\"altText\": \"SPC FLOORING\", \"imageUrl\": \"https://res.cloudinary.com/dsnhtfuef/image/upload/v1763712811/uploads/ipvfemweh66pt5nzhp8a.webp\", \"public_id\": \"uploads/ipvfemweh66pt5nzhp8a\", \"resource_type\": \"image\"}"}', '', 'SPC Flooring: Style Meets Practicality
', '{"{\"name\": \"\", \"detail\": \"For a look of modern style that stands up to real life, Two Guys Home Furnishings offers a full range of SPC flooring in the UAE. This advanced material gives you the beautiful, realistic look of wood or stone with far greater performance. It is engineered to be water resistant, highly scratch-resistant, and incredibly durable. This flooring is perfect for the busy Dubai lifestyle, offering a worry-free flooring solution that looks great for years.\"}","{\"name\": \"\", \"detail\": \"When it comes to installation, speed and precision are key. Our expert team installs SPC vinyl flooring quickly and perfectly. The result is a flawless finish that instantly improves your space, ready for your family from day one.\"}","{\"name\": \"\", \"detail\": \"Our SPC flooring solution includes four key points:\"}","{\"name\": \"\", \"detail\": \"<b>1. The Planks & Tiles:</b> Available in a huge range of realistic wood and stone designs. </br><b>2. Rigid Core Technology:</b> A strong, stable core that resists dents and provides stability.</br><b> 3. Professional Installation:</b> Our expert team ensures a perfect fit, often with a fast click-lock system.</br><b>4. Underlayment & Finishing:</b> We use high-quality materials for comfort and sound reduction.\"}"}', '{}', 'Collection', 'Our Bestselling SPC Flooring Dubai Styles', '{}', '{}', '{}', '{}', '{}', '', '', '', '', '', '', 'Dubai''s Choice for Practical, Stylish Flooring', 'Book a free consultation to see our SPC flooring samples, including our popular wood and stone effects.', NULL, '', 'Shop the best SPC flooring in Dubai. Our water-resistant and highly durable flooring offers the beautiful look of wood or stone. Free home visit.
', 'SPC Flooring | Water Resistant & Durable Floors', NULL, NULL, 'PUBLISHED');
