INSERT INTO public."Reviews" (id, "posterImageUrl", name, "starRating", "ReviewsDescription", "createdAt", "reviewDate", "updatedAt") VALUES (13, NULL, 'Jogesh Ajay', 5, 'I recently had curtains installed at my new house by Two Guys Home Furnishings, and I couldn''t be happier with the service! Wahab and Sukesh did an excellent job, handling the installation with great professionalism and attention to detail. Their expertise and friendly demeanor made the whole process smooth and stress-free. Highly recommend their services!', '2025-06-11 10:50:15.048', '2025-06-03', NULL);
