INSERT INTO public."Reviews" (id, "posterImageUrl", name, "starRating", "ReviewsDescription", "createdAt", "reviewDate", "updatedAt") VALUES (16, NULL, 'Yevheniia', 5, 'We ordered motorized blinds from this company. They installed it fully last week. So far everything is good and working. Employees are doing their job perfectly. Very professional attitude. We didn''t have any issues and completely satisfied with their job!', '2025-06-11 10:52:56.932', '2025-02-16', NULL);
INSERT INTO public."Reviews" (id, "posterImageUrl", name, "starRating", "ReviewsDescription", "createdAt", "reviewDate", "updatedAt") VALUES (17, NULL, 'Sara', 5, 'Amazing service by Humayoun and Renardo very professional and curtains look great', '2025-06-11 10:54:11.121', '2025-03-07', NULL);
